  console.log('üë§ Sender Name:', senderName);

  // Extract name from user input first
  if (userInput) {
    const userNamePatterns = [
      /(?:◊ê◊†◊ô|◊ß◊ï◊®◊ê◊ô◊ù ◊ú◊ô|◊î◊©◊ù ◊©◊ú◊ô|◊©◊û◊ô)\s+([◊ê-◊™]{2,15})/i,
      /^([◊ê-◊™]{2,10})$/,
      /◊î◊ô◊ô.*?◊ê◊†◊ô\s+([◊ê-◊™]{2,15})/i
    ];

    for (const pattern of userNamePatterns) {
      const match = userInput.match(pattern);
      if (match && match[1]) {
        const candidate = match[1].trim();
        const excludeWords = ['◊®◊ï◊¶◊î', '◊¶◊®◊ô◊ö', '◊û◊¢◊ï◊†◊ô◊ô◊ü', '◊ó◊ï◊©◊ë', '◊ò◊ï◊ë', '◊ô◊§◊î'];
        if (!excludeWords.includes(candidate)) {
          extractedName = candidate;
          console.log('‚úÖ Name from user input:', extractedName);
          break;
        }
      }
    }
  }

  // Extract name from AI response if not found in user input
  if (!extractedName && aiResponse) {
    const thankPattern = /◊™◊ï◊ì◊î\s+([◊ê-◊™]{2,15})/i;
    const thankMatch = aiResponse.match(thankPattern);
    if (thankMatch && thankMatch[1]) {
      extractedName = thankMatch[1];
      console.log('‚úÖ Name from "◊™◊ï◊ì◊î [◊©◊ù]" pattern:', extractedName);
    }

    if (!extractedName) {
      const greetingPattern = /(?:◊û◊ï◊©◊ú◊ù|◊†◊¢◊ô◊ù|◊°◊ë◊ë◊î),?\s+([◊ê-◊™]{2,15})/i;
      const greetingMatch = aiResponse.match(greetingPattern);
      if (greetingMatch && greetingMatch[1]) {
        extractedName = greetingMatch[1];
        console.log('‚úÖ Name from greeting pattern:', extractedName);
      }
    }
  }

  // Check for completion indicators
  const completionPhrases = [
    '◊™◊ï◊ì◊î',
    '◊û◊ï◊©◊ú◊ù', 
    '◊û◊¢◊ï◊ú◊î',
    '◊†◊ó◊ñ◊ï◊® ◊ê◊ú◊ô◊ö',
    '◊ê◊¢◊ë◊ô◊® ◊ú◊û◊ß◊¢◊ß◊¢◊™',
    '◊†◊ß◊ë◊¢ ◊ú◊ö',
    '◊†◊ô◊¶◊ï◊® ◊ß◊©◊®',
    '◊†◊™◊ê◊ù',
    '◊ê◊™ ◊î◊§◊®◊ò◊ô◊ù ◊©◊ú◊ö',
    '◊ë◊î◊ß◊ì◊ù'
  ];

  foundCompletions = completionPhrases.filter(phrase => aiResponse.includes(phrase));
  console.log('‚úÖ Completion phrases found:', foundCompletions);

  // Decision logic
  if (extractedName && foundCompletions.length > 0) {
    shouldTriggerSummary = true;
    triggerReason = `Name "${extractedName}" + ${foundCompletions.length} completion phrases: ${foundCompletions.join(', ')}`;
  } else if (foundCompletions.length >= 2) {
    shouldTriggerSummary = true;
    triggerReason = `Strong completion indicators (${foundCompletions.length}): ${foundCompletions.join(', ')}`;
  } else if (extractedName && aiResponse.includes('◊™◊ï◊ì◊î')) {
    shouldTriggerSummary = true;
    triggerReason = `AI thanking user "${extractedName}"`;
  } else {
    triggerReason = `Insufficient indicators - Name: "${extractedName}", Completions: ${foundCompletions.length}`;
  }

  console.log('üìä FINAL DECISION:');
  console.log('  Name:', extractedName || 'None');
  console.log('  Trigger:', shouldTriggerSummary);
  console.log('  Reason:', triggerReason);

} catch (error) {
  console.error('‚ùå Error:', error.message);
  shouldTriggerSummary = false;
  triggerReason = 'Error occurred: ' + error.message;
  foundCompletions = [];
}

